import unittest

from collections import Counter
class Solution:
    def largestPerimeter(self, A):
        """
        :type A: List[int]
        :rtype: int
        """
        # skip more than 3 of the same value, cause we won't use more than 3 in a triangle
        c = Counter(A)
        for k in c.keys():
            if c[k] > 3: c[k] = 3
        values = sorted(c.elements(), reverse=True)
        for i, first in enumerate(values):
            for j, second in enumerate(values[i+1:]):
                for third in values[i+j+2:]:
                    if first >= second + third:
                        break
                    else:
                        return first + second + third
        return 0


